import React from 'react';
import ReactDOM from 'react-dom';
import axios from 'axios';
import { BrowserRouter as Router, Route, Switch, Redirect, usehistory } from 'react-router-dom';

class Payment extends React.Component{
constructor(props){
super(props)
this.state={
test:'',
date:'',
flightnum:'',
pass_num:'',
gender:[],
fname:[],
lname:[],
phn_num:'',
email:'',
flightscon:[],
}
}

getVal=()=>{
this.setState({
test:'no',
date:this.props.location.state.arr[4],
flightnum:this.props.location.state.arr[2],
pass_num:this.props.location.state.arr[3],
phn_num:this.props.location.state.arr[5],
email:this.props.location.state.arr[6]
})
this.state.gender=this.props.location.state.arr[7];
this.state.fname=this.props.location.state.arr[8];
this.state.lname=this.props.location.state.arr[9];
}

redirect=()=>{
const arr=this.state.data;
this.props.history.push('/Confirmation',{arr});
}

subhand = async event => {
  event.preventDefault();

  axios.get("http://localhost:8080/fbooking/?date="+this.state.date+"&fselect="+this.state.flightnum+"&pass_num="+this.state.pass_num+"&gender="+this.state.gender+"&first_name="+this.state.fname+"&first_name="+this.state.fname+"")
  .then(
    response=>{
        console.log(response);
        this.setState({flightscon:response.data});
        this.setState({submitted:'true'});
    }
  )
}


render(){
console.log(this.props.location.state.arr);
return(
<div>
<center><h1>PAYMENT PAGE</h1></center>
<center><button onClick={this.subhand}>PROCEED</button></center>
</div>
)
}
}

export default Payment;
