import React from 'react';
import ReactDOM from 'react-dom';
import Di from './Di.js';
import { withRouter } from "react-router";


class Flight_result extends React.Component{
_isMounted=false;
constructor(props){
super(props)
this.state={
flights:[]
};
}
componentDidMount() {
  this._isMounted = true;
  this.setState({flights:this.props.location.state.flights});
}

render(){
return(
<div>
    <h1>Available Flights</h1>
    <div>
     <table className="table">
     <thead>
     <tr>
     <th>Departure</th>
     <th>Arrival</th>
     <th>Flight Number</th>
     </tr>
     </thead>
     <tbody>
      {
       this.props.location.state.flights.map(
       flight =>
       <tr key={flight.flight_num}>
       <td>{flight.dep}</td>
       <td>{flight.arr}</td>
       <td>{flight.flight_num}</td>
        </tr>
         )
        }
        </tbody>
        </table>
        </div>
        </div>
        );
    }
}

export default Flight_result;
