import React from 'react';
import ReactDOM from 'react-dom';
import Di from './Di.js';

class Flight_result extends React.Component{
constructor(props){
super(props)
this.state={
dep:'',
arr:'',
flight_num:'',
}
}


onSubmitHandler=(event)=>{
let name=event.target.name;
let value=event.target.value;
this.setState({[name]:value})
console.log(this.state.dep);
console.log(this.state.arr);
console.log(this.state.flight_num);
}

render(){
return(
<div>
    <h1>Available Flights</h1>
    <div>
    <form onSubmit={this.onSubmitHandler}>
     <table className="table">
     <thead>
     <tr>
     <th>Departure</th>
     <th>Arrival</th>
     <th>Flight Number</th>
     </tr>
     </thead>
     <tbody>
      {
       console.log(this.props.location.state.arr1),
       this.props.location.state.arr1.map(
       flight =>
       <tr key={flight.flight_num}>
       <td><input type="hidden" name="dep" value={flight.dep}/>{flight.dep}</td>
       <td><input type="hidden" name="arr" value={flight.arr}/>{flight.arr}</td>
       <td><input type="hidden" name="flight_num" value={flight.flight_num}/>{flight.flight_num}</td>
       <td><input type="Submit" name="Submit" value="Submit"/></td>
        </tr>
         )
        }
        </tbody>
        </table>
        </form>
        </div>
        </div>
        );
    }
}

export default Flight_result;
