    this.setState({[nam]: val});
  }
  subhand = (event) => {
  event.preventDefault();
  this.setState({submitted:'true'});
  axios.get("http://localhost:8080/fdetails/?date="+this.state.date+"&from="+this.state.from+"&nos="+this.state.nos+"&nos1=0&to="+this.state.to+"")
  .then(
    response=>{
