import React from 'react';
import ReactDOM from 'react-dom';
import Di from './Component/Di.js';
import './App.css';
import Flight_result from './Component/Flight_result.js';
import { BrowserRouter as Router, Route, Switch } from 'react-router-dom';

class App extends React.Component {
constructor(props){
super(props)
this.state={
flights:[]
}

getResults=(data)=>{
this.setstate({flights:data})
}

}
 render(){
 return (
    <Router>
     <Switch>
     <Route exact path="/" component={Di} />
     <Di getdata={this.getResults}/>
     <Route path="/Results" render={props=>(<Flight_result flights={this.state.flights}{...props} />)}/>
      </Switch>
    </Router>
     );
   }
}

export default App;
