import React from 'react';
import ReactDOM from 'react-dom';

class Pinfo extends React.Component{
constructor(props){
super(props)
this.state={
ta:[],
data:[],
gender:[],
fname:[],
lname:[],
}
}

add_info=(event)=>{
this.state.data=this.props.location.state.arr;
this.state.data.push("Add");
console.log(this.state.data);
}

createArrayform=()=>{
let length=this.props.location.state.arr.length;
let index=this.props.location.state.arr[length-1];
for(let i=0;i<index;i++){
this.state.ta[i]=i;
}
console.log(this.state.ta);
}

onChangeHandler=(event)=>{
let name=event.target.name;
let value=event.target.value;
this.state.name.push(value);
}

onSubmitHandler=(event)=>{
event.preventDefault();
console.log(event.target.value);
console.log(event.currentTarget.innerHTML);
console.log(this.state.gender );
}

redirect=()=>{
const arr=this.state.data;
this.props.history.push('/Pinfo',{arr});
}



render(){
console.log(this.props.location.state.arr);
return(
<div>
{this.createArrayform()}
<div>
<div>
<form onSubmit={this.onSubmitHandler}>
<input type='radio' name='gender' value='male' onChange={this.onSubmitHandler}/>Mr.
<input type='radio' name='gender' value='female' onChange={this.onSubmitHandler} />Mrs.
<input type='text' name='fname' onChange={this.onSubmitHandler} />First Name
<input type='text' name='lname' onChange={this.onSubmitHandler} />Last Name
<input type='submit'/>
</form>
</div>
</div>
</div>
);
}
}

export default Pinfo;
