import React from 'react';
import ReactDOM from 'react-dom';
import Di from './Di.js';
import { BrowserRouter as Router, Route, Switch, Redirect, usehistory } from 'react-router-dom';

class Flight_result extends React.Component{
constructor(props){
super(props)
this.state={
dep:'',
arr:'',
flight_num:'',
length:'',
index:'',
data:[],
submitted:'false',
}
}

onSubmitHandler=(event)=>{
console.log(event.currentTarget.innerHTML);
let data=event.currentTarget.innerHTML;
let val=data.split('<td>');
for(let i=1;i<val.length-1;i++){
let val1=val[i].split("</td>");
this.state.data.push(val1[0]);
}
console.log(this.state.data);
this.setState({submitted:'true'})
}

redirect=()=>{
const arr=this.state.data;
this.props.history.push('/Pinfo',{arr});
}

render(){
if(this.state.submitted==='true'){
{this.redirect()}
}
return(
<div>
    <h1>Available Flights</h1>
     <table className="table">
     <thead>
     <tr>
     <th>Departure</th>
     <th>Arrival</th>
     <th>Flight Number</th>
     </tr>
     </thead>
     </table>
      {
       console.log(this.props.location.state.arr1),
       this.props.location.state.arr1.map(
       (flight,index)=>
       <form onSubmit={this.onSubmitHandler}>
       <div>
       <table>
       <tbody>
       <tr>
       <td color='red'><p fontcolor='red'>{flight.dep}</p></td>
       <td>{flight.arr}</td>
       <td>{flight.flight_num}</td>
       <td><button>Submit</button></td>
        </tr>
        </tbody>
        </table>
        </div>
        </form>
        )
        }
        </div>
        );
    }
}

export default Flight_result;
