import React from 'react';
import ReactDOM from 'react-dom';

class Pinfo extends React.Component{
constructor(props){
super(props)
this.state={
gender:'',
fname:'',
lname:'',
ta:[],
data:[],
gender:[],
fname:[],
lname:[],
}
}

add_info=(event)=>{
this.state.data=this.props.location.state.arr;
this.state.data.push("Add");
console.log(this.state.data);
}

createArrayform=()=>{
let length=this.props.location.state.arr.length;
let index=this.props.location.state.arr[length-1];
for(let i=0;i<index;i++){
this.state.ta[i]=i;
}
console.log(this.state.ta);
}

onUpdate=(event)=>{
let nam=event.target.name;
let val=event.target.value;
this.setState({[nam]:value});
}

onSubmitHandler=(event)=>{
event.preventDefault();
if(name==="gender"){
this.state.gender.push(this.state.gender);
}
else if(name==="fname"){
this.state.fname.push(this.state.fname);
}
else if(name==="lname"){
this.state.lname.push(this.state.lname);
}
console.log(this.state.gender);
console.log(this.state.fname);
console.log(this.state.lname);
}

redirect=()=>{
const arr=this.state.data;
this.props.history.push('/Pinfo',{arr});
}



render(){
console.log(this.props.location.state.arr);
return(
<div>
{this.createArrayform()}
<div>
<div>
<form onSubmit={this.onSubmitHandler}>
<input type='radio' name='gender' value='male' onChange={this.onUpdate}/>Mr.
<input type='radio' name='gender' value='female' onChange={this.onUpdate} />Mrs.
<input type='text' name='fname' onChange={this.onUpdate} />First Name
<input type='text' name='lname' onChange={this.onUpdate} />Last Name
<input type='submit'/>
</form>
</div>
</div>
</div>
);
}
}

export default Pinfo;
