import React from 'react';
import ReactDOM from 'react-dom';
import flightdetails from '../Service/axios.js';
import axios from "axios";

class Di extends React.Component {
  constructor(props) {
    super(props);
    //this.refreshCourses = this.refreshCourses.bind(this)
    this.state = { from: '',
    to: '',
    date: '',
    nos: '',
    flights:[]
    };
  }
//   componentDidMount() {
//      this.refreshCourses();
//    }
//    refreshCourses(){
//        let name="?date="+this.state.date+"&from="+this.state.from+"&nos="+this.state.nos+"&nos1=0&to="+this.state.to+"";
//        flightdetails.retrieveflights(name)
//        .then(
//            response => {
//              console.log(response);
//            }
//        )
//    }
  myChangeHandler = (event) => {
    let nam= event.target.name;
    let val=event.target.value;
    this.setState({[nam]: val});
  }
  subhand = (event) => {
  event.preventDefault();
  axios.get("http://localhost:8080/fdetails/?date=15/02/2020&from=Bengaluru&nos=1&nos1=0&to=Raipur")
  .then(
    response=>{
        this.setState({flights:response.data})
    }
  )

  }

  render() {
    return (
      <form onSubmit={this.subhand}>
      <h1>From: {this.state.from}, To: {this.state.to}, On: {this.state.date}, NUM: {this.state.nos}</h1>
      <p>Enter Departure:</p>
      <input
        type='text'
        name='from'
        onChange={this.myChangeHandler}
      />
      <p>Enter Destination:</p>
      <input
        type='text'
        name='to'
        onChange={this.myChangeHandler}
        />
        <p>Enter Date</p>
        <input
        type='text'
        name='date'
        onChange={this.myChangeHandler}
        />
        <p>Enter number of passenger</p>
        <input
        type='text'
        name='nos'
        onChange={this.myChangeHandler}
        />
        <br></br>
        <input
        type='submit'
        />
      </form>
    );
  }
}

export default Di;
