import React from 'react';
import ReactDOM from 'react-dom';
import flightdetails from '../Service/axios.js';
import axios from "axios";

class Di extends React.Component {
  constructor(props) {
    super(props);
    //this.refreshCourses = this.refreshCourses.bind(this)
    this.state = { from: '',
    to: '',
    date: '',
    nos: '',
    flights:[],
    submitted:'false'
    };
  }
//   componentDidMount() {
//      this.refreshCourses();
//    }
//    refreshCourses(){
//        let name="?date="+this.state.date+"&from="+this.state.from+"&nos="+this.state.nos+"&nos1=0&to="+this.state.to+"";
//        flightdetails.retrieveflights(name)
//        .then(
//            response => {
//              console.log(response);
//            }
//        )
//    }
  myChangeHandler = (event) => {
    let nam= event.target.name;
    let val=event.target.value;
    this.setState({[nam]: val});
  }
  subhand = (event) => {
  event.preventDefault();
  this.setState({submitted:'true'});
  axios.get("http://localhost:8080/fdetails/?date="+this.state.date+"&from="+this.state.from+"&nos="+this.state.nos+"&nos1=0&to="+this.state.to+"")
  .then(
    response=>{
        console.log(response);
        this.setState({flights:response.data});
    }
  )
  }

  render() {
    if(this.state.submitted=="true"){
    return(
    <div>
    <h1>Available Flights</h1>
    <div>
     <table className="table">
     <thead>
     <tr>
     <th>Departure</th>
     <th>Arrival</th>
     </tr>
     </thead>
     <tbody>
      {
      this.state.flights.map(
       flight =>
       <tr key={flight.dep}>
       <td>{flight.dep}</td>
       <td>{flight.arr}</td>
        </tr>
         )
         }
        </tbody>
        </table>
        </div>
        </div>
    );
    }
    else{
    return (
      <form onSubmit={this.subhand}>
      <h1>From: {this.state.from}, To: {this.state.to}, On: {this.state.date}, NUM: {this.state.nos} </h1>
      <p>Enter Departure:</p>
      <input
        type='text'
        name='from'
        onChange={this.myChangeHandler}
      />
      <p>Enter Destination:</p>
      <input
        type='text'
        name='to'
        onChange={this.myChangeHandler}
        />
        <p>Enter Date</p>
        <input
        type='text'
        name='date'
        onChange={this.myChangeHandler}
        />
        <p>Enter number of passenger</p>
        <input
        type='text'
        name='nos'
        onChange={this.myChangeHandler}
        />
        <br></br>
        <input
        type='submit'
        />
      </form>
    );
    }
  }
}

export default Di;
