import React from 'react';
import ReactDOM from 'react-dom';

class Pinfo extends React.Component{
constructor(props){
super(props)
this.state={
gender:'',
fname:'',
lname:'',
counter:'',
ta:[],
data:[],
arrgender:[],
arrfname:[],
arrlname:[],
}
}

add_info=(event)=>{
this.state.data=this.props.location.state.arr;
this.state.data.push("Add");
console.log(this.state.data);
}

onUpdate=(event)=>{
let nam=event.target.name;
let val=event.target.value;
this.setState({[nam]:val});
}

onSubmitHandler=(event)=>{
event.preventDefault();
this.state.arrgender.push(this.state.gender);
this.state.arrfname.push(this.state.fname);
this.state.arrlname.push(this.state.lname);
console.log(this.state.arrgender);
console.log(this.state.arrfname);
console.log(this.state.arrlname);
this.state.counter=this.state.counter+1;
console.log(this.state.counter);
}

redirect=()=>{
const arr=this.state.data;
this.props.history.push('/Pinfo',{arr});
}



render(){
console.log(this.props.location.state.arr);
let length=this.props.location.state.arr.length;
let index=this.props.location.state.arr[length-1];
if(this.state.counter<index){
return(
<div>
<h1>Form+{this.state.counter}</h1>
<form onSubmit={this.onSubmitHandler}>
<input type='radio' name='gender' value='male' onChange={this.onUpdate}/>Mr.
<input type='radio' name='gender' value='female' onChange={this.onUpdate} />Mrs.
<input type='text' name='fname' onChange={this.onUpdate} />First Name
<input type='text' name='lname' onChange={this.onUpdate} />Last Name
<input type='Submit'/>
</form>
</div>
)
}
return(
<div>
<h1>FORM Submission successful</h1>
</div>
);
}
}

export default Pinfo;
