import React from 'react';
import ReactDOM from 'react-dom';
import Di from './Di.js';

class Flight_result extends React.Component{
constructor(props){
super(props)
this.state={
dep:'',
arr:'',
flight_num:'',
submitted:'false',
}
}

onSubmitHandler=(event)=>{
event.preventDefault();
this.setState({submitted:'true'})
}

onChangeHandler=(event)=>{
let name=event.target.name;
let value=event.target.value;
this.setState({[name]:value})
}

render(){
if(this.state.submitted==='true'){
return(
<div>
{
console.log(this.state.dep),
console.log(this.state.arr),
console.log(this.state.flight_num)
}
</div>
);
}
return(
<div>
    <h1>Available Flights</h1>
    <div>
    <form onSubmit={this.onSubmitHandler}>
     <table className="table">
     <thead>
     <tr>
     <th>Departure</th>
     <th>Arrival</th>
     <th>Flight Number</th>
     </tr>
     </thead>
     <tbody>
      {
       console.log(this.props.location.state.arr1),
       this.props.location.state.arr1.map(
       flight =>
       <tr key={flight.flight_num}>
       <td><input type="hidden" name="dep" placeholder="dep" value={flight.dep} ref={input=>this.dep=input}/>{flight.dep}</td>
       <td><input type="hidden" name="arr" onChange={this.onChangeHandler} value={flight.arr}/>{flight.arr}</td>
       <td><input type="hidden" name="flight_num" onChange={this.onChangeHandler} value={flight.flight_num}/>{flight.flight_num}</td>
       <td><input type="Submit"/></td>
        </tr>
         )
        }
        </tbody>
        </table>
        </form>
        </div>
        </div>
        );
    }
}

export default Flight_result;
