import React from 'react';
import ReactDOM from 'react-dom';
import Di from './Di.js';

class Flight_result extends React.Component{
constructor(props){
super(props)
this.state={
dep:'',
arr:'',
flight_num:'',
submitted:'false',
}
}

onSubmitHandler=(event)=>{
event.preventDefault();
this.setState({submitted:'true'})
}

onChangeHandler=(event)=>{
let name=event.target.name;
let value=event.target.value;
this.setState({[name]:value})
}

redirect=()=>{
const arr={
dep:this.state.dep.value,
arr:this.state.arr.value,
flight_num:this.state.flight_num.value,
}
}

render(){
if(this.state.submitted==='true'){
return(
<div>
{
console.log(this.state.dep.value),
console.log(this.state.arr.value),
console.log(this.state.flight_num.value)
}
</div>
);
}
return(
<div>
    <h1>Available Flights</h1>
    <div>
    <form onSubmit={this.onSubmitHandler}>
     <table className="table">
     <thead>
     <tr>
     <th>Departure</th>
     <th>Arrival</th>
     <th>Flight Number</th>
     </tr>
     </thead>
     <tbody>
      {
       console.log(this.props.location.state.arr1),
       this.props.location.state.arr1.map(
       (flight,index) =>
       <tr key={index}>
       <td><input type="hidden" name="dep" value={flight.dep[index]} ref={input=>this.state.dep=input}/>{flight.dep+index}</td>
       <td><input type="hidden" name="arr" value={flight.arr} ref={input=>this.state.arr=input}/>{flight.arr}</td>
       <td><input type="hidden" name="flight_num" value={flight.flight_num} ref={input=>this.state.flight_num=input}/>{flight.flight_num}</td>
       <td><input type="Submit"/></td>
        </tr>
         )
        }
        </tbody>
        </table>
        </form>
        </div>
        </div>
        );
    }
}

export default Flight_result;
