import React from 'react';
import ReactDOM from 'react-dom';

class Pinfo extends React.Component{
constructor(props){
super(props)
this.state={
ta:[],
data:[],
gender:[],
}
}

add_info=(event)=>{
this.state.data=this.props.location.state.arr;
this.state.data.push("Add");
console.log(this.state.data);
}

createArrayform=()=>{
let length=this.props.location.state.arr.length;
let index=this.props.location.state.arr[length-1];
for(let i=0;i<index;i++){
this.state.ta[i]=i;
}
console.log(this.state.ta);
}

onSubmitHandler=(event)=>{
event.preventDefault();
console.log(event.target.value);
console.log(event.currentTarget.innerHTML);
console.log(this.state.gender );
}

redirect=()=>{
const arr=this.state.data;
this.props.history.push('/Pinfo',{arr});
}



render(){
console.log(this.props.location.state.arr);
return(
<div>
{this.createArrayform()}
<div>
{
this.state.ta.map(
(inp,index)=>
<div>
<form onSubmit={this.onSubmitHandler}>
<table>
<tr>
<td><input type='radio' name='gender' value='male'  ref={input=>this.state.gender[{index}]=input.value}/>Mr.</td>
<td><input type='radio' name='gender' value='female'  ref={input=>this.state.gender[{index}]=input.value}/>Mrs.</td>
<td><input type='text' name='fname' onChange={this.onSubmitHandler} />First Name</td>
<td><input type='text' name='lname' onChange={onSubmitHandler} />Last Name</td>
</tr>
</table>
<input type='submit'/>
</form>
</div>
)
}
</div>
</div>
);
}
}

export default Pinfo;
