import React from 'react';
import ReactDOM from 'react-dom';

class Di extends React.Component {
  constructor(props) {
    super(props);
    this.state = { from: '',
    to: '',
    date: '',
    nos: '',
    };
  }
  myChangeHandler = (event) => {
    let name=this.event.name;
    let value=this.event.value;
    this.setState({name: value});
  }
  render() {
    return (
      <form>
      <h1>Hello {this.state.username}</h1>
      <p>Enter Departure:</p>
      <input
        type='text'
        name='from'
        onChange={this.myChangeHandler}
      />
      <p>Enter Destination:</p>
      <input
        type='text'
        name='to'
        onChange={this.myChangeHandler}
        />
        <input
        type='text'
        name='date'
        onChange={this.myChangeHandler}
        />
        <input
        type='text'
        name='nos'
        onChange={this.myChangeHandler}
        />
      </form>
    );
  }
}

export default Di;
